function submit_form(a){$("#"+a).submit()}function sort_field_abc(b){var a=$("#"+b+" li").get();a.sort(function(g,e){var f=$(g).text();var d=$(e).text();if(f<d){return -1}if(f>d){return 1}return 0});var c=$("#"+b);$.each(a,function(e,d){c.append(d)})}function sort_field_attribute(a){var c=$("#"+a);var b=c.find("li").sort(function(e,d){return $(d).attr("data-sort")-$(e).attr("data-sort")});c.find("li").remove();c.append(b)}function toggle_show_more(a){$("."+a).toggleClass("hidden")}function field_filter(){var d,g,f,b,c,e;d=document.getElementById("field_search");g=d.value.toUpperCase();f=document.getElementById("jobField");b=f.getElementsByTagName("li");for(e=0;e<b.length;e++){c=b[e].getElementsByTagName("a")[0];if(c.innerHTML.toUpperCase().indexOf(g)>-1){b[e].style.display=""}else{b[e].style.display="none"}}};